mapper.insert:
 	insert into
 		orders
 		(
    		 orderCode,
    		 storeCode,
    		  memberId, 
    		 orderState
 		)
 	values
 		(
    		to_char(sysdate, 'yyyymmdd') || order_seq.nextval || #{storeCode}
    		, #{storeCode}
    		 , #{memberId} 
    		, 'apply'
	 		)

insert into
	orderTemp
		(tempOrderkey
		, orderCode
		, storeCode
		, menuNum
		, menuName
		, pricePerOne
		, orderCount)
	values
		(#{tempOrderkey}
		, #{orderCode}
		, #{storeCode}
		, #{menuNum}
		, #{menuName}
		, #{pricePerOne}
		, #{orderCount})

mapper.update:
		update 
			orders
		set
			, orderDate = sysdate
			, totalPrice = #{totalPrice}
			, 
            orderState = #{orderState}
            , 
		 	 menuList = #{menuList}
		where 
			storeCode = #{storeCode}
			and memberid = #{memberid}
