mapper.insert:
 	insert into 
 		orders
 		(
    		 orderCode,
    		 storeCode,
    		 orderDate,
    		 memberId,
    		 totalPrice,
    		 menuList,
    		 orderState
 		)
 	values
 		(
    		to_char(sysdate, 'yyyymmdd') || order_seq.nextval || #{storeCode}
    		,#{storeCode}
    		,to_char(sysdate, 'yyyymmdd')
    		,#{memberId}
    		,#{totalPrice}
    		,#{menuList}
    		,#{orderState}
	 		)

insert into
	orderTemp
		(tempOrderkey
		, orderCode
		, menuNum
		, menuName
		, pricePerOne
		, orderCount)
	values
		(#{tempOrderkey}
		, #{orderCode}
		, #{menuNum}
		, #{menuName}
		, #{pricePerOne}
		, #{orderCount})

mapper.update:
		update 
			order
		set
			, orderDate = sysdate
			, totalPrice = #{totalPrice}
			, 
            orderState = #{orderState},

		 	 menuList = #{menuList}
		where 
			storeCode = #{storeCode}
			and memberid = #{memberid}
