mapper.update:
update
	storeInfo
set
	storeName = #{storeName}
	, address1 = #{address1}
	, address2 = #{address2}
	, postCode = #{postCode}
	, brn = #{brn}
	, Phone = #{Phone}
	, runningTime = #{runningTime}
	, dayOff = #{dayOff}
	, info = #{info}
	, savedFilename = #{savedFilename}
where
	memberId = #{memberId}

update
	reservation
set
	reserveState = #{reserveState}
where
	reserveCode = #{reserveCode}

mapper.insert:
insert into
	reservation
		(
		reserveCode
		, storeCode
		, memberId
		,  
		reservationName
		, reservationPhone
		, reserveDate
		, reserveHead
		, request
		,  
		reserveState
		)
values (
		to_char(sysdate, 'yyyymmdd') || #{storeCode} || reservation_seq.nextval
		, #{storeCode}
		, #{memberId}
		, 
		#{reservationName}
		, #{reservationPhone}
		, to_date(#{reserveDate}, 'yyyy-mm-dd hh24:mi')
		, #{reserveHead}
		, #{request}
		,  
		#{reserveState}
		)

insert into storeInfo 
	(storeCode
	, memberId
	, storeName
	, address1
	, address2
	, postCode
	, brn
	, phone
	, runningTime
	, dayOff
	, info
	 , originFilename
	, savedFilename 
	)
values 
	( storeInfo_seq.nextval
	, #{memberId}
	, #{storeName}
	, #{address1}
	, #{address2}
	, #{postCode}
	, #{brn}
	, #{phone}
	, #{runningTime}
	, #{dayOff}
	, #{info}
	 , #{originFilename}
	, #{savedFilename} 
	)
