mapper.insert:
insert into Orders 
	( orderCode
	, storeCode
	, memberId
	, totalPrice
	, menuList
	, orderState
	)
values
	( #{orderCode}
	, #{storeCode}
	, #{memberId}
	, #{totalPrice}
	, #{menuList}
	, #{orderState}
	)

insert into menu 
	(menuNum
	, storeCode
	, menuName
	, price
	, category
	 , menuInfo 
	 , originFilename
	, savedFilename  )
values 
	( menu_seq.nextval
	, #{storeCode}
	, #{menuName}
	, #{price}
	, #{category}
	 , #{menuInfo} 
	 , #{originFilename}
	, #{savedFilename} )
